import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { environment } from '../../environments/environment';

@Component({
  selector: 'app-clock-list',
  templateUrl: './clock-list.component.html'
})
export class ClockListComponent {
  public clocks: ClockData[] = [];

  constructor(http: HttpClient) {
    http.get<ClockData[]>(environment.apiBaseUrl + 'worldclocks')
      .subscribe({
        next: result=> this.clocks = result,
        error: error => console.error(error)
      });
  }
}

interface ClockData {
  instant: Date;
  localDateTime: Date;
  timezone: string;
  zonedDateTime: Date;
}
<p *ngIf="!clocks?.length"><em>Loading...</em></p>

<table class='table table-striped' aria-labelledby="tableLabel" *ngIf="clocks.length">
  <thead>
    <tr>
      <th>instant</th>
      <th>localDateTime</th>
      <th>timezone</th>
      <th>zonedDateTime</th>
    </tr>
  </thead>
  <tbody>
    <tr *ngFor="let clock of clocks">
      <td>{{ clock.instant }}</td>
      <td>{{ clock.localDateTime }}</td>
      <td>{{ clock.timezone }}</td>
      <td>{{ clock.zonedDateTime }}</td>
    </tr>
  </tbody>
</table>
